  const [nonce, setNonce] = useState('');

  useEffect(() => {
    // Get nonce from meta tag
    const metaNonce =
      document
        .querySelector('meta[name="csp-nonce"]')
        ?.getAttribute('content') || '';
    setNonce(metaNonce);
  }, []);
  const colorTheme = {
    green: {
      gradient: 'from-emerald-400 via-teal-500 to-cyan-600',
