'use client';

import { useCallback, useRef } from 'react';

import { useGDPR } from '@/contexts/gdpr-context';

interface BookingButtonProps {
  id: string;
  dataSrc: string;
  className?: string;
  children: React.ReactNode;
  trackingLabel?: string; // For identifying which button was clicked
  packageName?: string; // For enhanced ecommerce tracking
  packagePrice?: string; // For conversion value tracking
}

export default function BookingButton({
  id,
  dataSrc,
  className,
  children,
  trackingLabel = 'Unknown',
  packageName = 'Unknown Package',
  packagePrice = '0',
}: BookingButtonProps) {
  const clickedButtonRef = useRef<HTMLButtonElement | null>(null);
  const bokunReadyAttempts = useRef(0);
  const { consent } = useGDPR();

  const ensureBokunIsReadyAndOpen = useCallback(() => {
    if (
      window.BokunWidgets &&
      (typeof window.BokunWidgets.init === 'function' ||
        typeof window.BokunWidgets.reinit === 'function')
    ) {
      // Check if a modal is already open (very basic check, might need refinement)
      if (
        document.querySelector('.bokunModalContainer') ||
        document.querySelector('.bokun-modal-open')
      ) {
        return;
      }

      // Ensure Bokun is ready before attempting to open
      try {
        if (typeof window.BokunWidgets.reinit === 'function') {
          // Try calling reinit without parameters (safer)
          window.BokunWidgets.reinit();
        }
      } catch (error) {
        if (process.env.NODE_ENV === 'development') {
          console.warn('[Booking Button] Error reinitializing Bokun:', error);
        }
      }

      if (clickedButtonRef.current) {
        // It's possible Bokun's scripts have now attached proper listeners.
        // A direct click might be better than trying to call their internal modal functions.
        clickedButtonRef.current.click();
        clickedButtonRef.current = null; // Clear after attempting
      }
      bokunReadyAttempts.current = 0; // Reset attempts
    } else if (bokunReadyAttempts.current < 30) {
      // Try for ~3 seconds
      bokunReadyAttempts.current++;
      setTimeout(ensureBokunIsReadyAndOpen, 100);
    } else {
      bokunReadyAttempts.current = 0; // Reset attempts
    }
  }, []);

  // Comprehensive tracking function for GDPR-compliant analytics
  const trackBookingClick = useCallback(() => {
    if (typeof window === 'undefined' || !consent) {
      return;
    }

    // Extract numeric price for conversion tracking
    const numericPrice =
      Number.parseFloat(packagePrice.replace(/[^\d.]/g, '')) || 0;

    // Google Analytics 4 Event Tracking (only if analytics consent given)
    if (window.gtag && consent.analytics) {
      // Standard GA4 event
      window.gtag('event', 'book_now_click', {
        event_category: 'Booking',
        event_label: trackingLabel,
        package_name: packageName,
        package_price: numericPrice,
        currency: 'EUR',
        button_id: id,
        page_location: window.location.href,
        page_title: document.title,
      });

      // Enhanced Ecommerce - Begin Checkout Event
      window.gtag('event', 'begin_checkout', {
        currency: 'EUR',
        value: numericPrice,
        items: [
          {
            item_id: id,
            item_name: packageName,
            item_category: 'Adventure Package',
            price: numericPrice,
            quantity: 1,
          },
        ],
      });

      // Google Ads Conversion Tracking
      const googleAdsConversionId =
        process.env.NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_ID;
      const googleAdsConversionLabel =
        process.env.NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_LABEL;

      if (googleAdsConversionId && googleAdsConversionLabel) {
        window.gtag('event', 'conversion', {
          send_to: `${googleAdsConversionId}/${googleAdsConversionLabel}`,
          value: numericPrice,
          currency: 'EUR',
          transaction_id: `booking_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        });
      } else if (process.env.NODE_ENV === 'development') {
        console.warn(
          '[Booking Tracking] Google Ads conversion tracking not configured. Please set NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_ID and NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_LABEL environment variables.',
        );
      }
    }

    // Vercel Analytics (if available and analytics consent given)
    if (window.va && consent.analytics) {
      type VercelAnalytics = (
        event: string,
        data: { name: string; data: Record<string, unknown> },
      ) => void;
      (window.va as VercelAnalytics)('event', {
        name: 'Book Now Click',
        data: {
          package: packageName,
          price: numericPrice,
          button_id: id,
          label: trackingLabel,
        },
      });
    }

    // Facebook Pixel (if available and marketing consent given)
    if (window.fbq && consent.marketing) {
      window.fbq('track', 'InitiateCheckout', {
        content_name: packageName,
        content_category: 'Adventure Package',
        value: numericPrice,
        currency: 'EUR',
      });
    }

    if (process.env.NODE_ENV === 'development') {
      console.log(
        `[Booking Tracking] ${trackingLabel} clicked - Package: ${packageName}, Price: €${numericPrice}`,
        'Consent:',
        consent,
      );
    }
  }, [trackingLabel, packageName, packagePrice, id, consent]);

  const handleBookNowClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    // Track the click immediately
    trackBookingClick();

    clickedButtonRef.current = event.currentTarget;
    ensureBokunIsReadyAndOpen(); // Direct call as loader is global
  };

  return (
    <button
      className={`
        bokunButton
        ${className}
      `}
      id={id}
      data-src={dataSrc}
      data-testid="widget-book-button"
      onClick={handleBookNowClick}
    >
      {children}
    </button>
  );
}

'use client';

import Script from 'next/script';
import { useEffect } from 'react';

import { useGDPR } from '@/contexts/gdpr-context'; // Import CookieConsent

interface GDPRGoogleAnalyticsProps {
  gaId: string;
}

export default function GDPRGoogleAnalytics({
  gaId,
}: GDPRGoogleAnalyticsProps) {
  const { consent } = useGDPR(); // Get the consent object

  useEffect(() => {
    // This effect handles applying consent changes to gtag
    if (typeof window !== 'undefined' && window.gtag && consent) {
      window.gtag('consent', 'update', {
        analytics_storage: consent.analytics ? 'granted' : 'denied',
        ad_storage: consent.marketing ? 'granted' : 'denied', // Assuming marketing consent maps to ad_storage
        ad_user_data: consent.marketing ? 'granted' : 'denied',
        ad_personalization: consent.marketing ? 'granted' : 'denied',
      });
      console.log('[GDPR GA] Consent updated:', consent);
    }
  }, [consent]); // Re-run when consent object changes

  // Initialize gtag in a CSP-compliant way
  useEffect(() => {
    if (typeof window !== 'undefined') {
      // Initialize dataLayer and gtag function
      window.dataLayer = window.dataLayer || [];
      function gtag(...args: unknown[]) {
        window.dataLayer?.push(args);
      }
      window.gtag = gtag;

      // Set default consent to denied
      gtag('consent', 'default', {
        analytics_storage: 'denied',
        ad_storage: 'denied',
        ad_user_data: 'denied',
        ad_personalization: 'denied',
        wait_for_update: 500,
      });

      gtag('js', new Date());
      gtag('config', gaId, {
        page_title: document.title,
        page_location: window.location.href,
      });

      console.log('[GDPR GA] gtag initialized with default denied consent');
    }
  }, [gaId]);

  return (
    <>
      {/* External gtag script - CSP compliant */}
      <Script
        id="ga-gtag-script"
        src={`https://www.googletagmanager.com/gtag/js?id=${gaId}`}
        strategy="afterInteractive"
        onLoad={() => {
          console.log('[GDPR GA] gtag.js loaded.');
        }}
        onError={() => {
          console.error('[GDPR GA] Failed to load gtag.js.');
        }}
      />
    </>
  );
}

'use client';

import { useState } from 'react';

import { PriceListPopup } from '@/components/ui/PriceListPopup';

interface PriceListButtonProps {
  text: string;
}

export default function PriceListButton({ text }: PriceListButtonProps) {
  const [isPriceListOpen, setIsPriceListOpen] = useState(false);

  return (
    <>
      <button
        onClick={() => setIsPriceListOpen(true)}
        className={`
          mt-4 mb-8 rounded-lg bg-[#6b8362] px-8 py-3 text-lg font-semibold
          text-white shadow-md transition-colors duration-300
          hover:bg-[#3E5A35] hover:shadow-lg
          md:mb-12
        `}
        aria-label="Open price list"
      >
        {text}
      </button>

      <PriceListPopup
        isOpen={isPriceListOpen}
        onClose={() => setIsPriceListOpen(false)}
      />
    </>
  );
}

'use client';

import { ChevronDown, Globe, Phone } from 'lucide-react';
import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { useEffect, useRef, useState } from 'react';

import { useLanguage } from '@/contexts/language-context';

export default function DesktopMenu() {
  const { t, language, setLanguage } = useLanguage();
  const pathname = usePathname();
