import { NextResponse } from 'next/server'

export async function POST(request: Request) {
  try {
    const report = await request.json()
    console.warn('CSP Violation:', report)
    // In a real application, you would send this report to a logging service
    // like Sentry, Report URI, or a custom analytics platform.
    return NextResponse.json({ message: 'CSP report received' }, { status: 200 })
  } catch (error) {
    console.error('Error processing CSP report:', error)
    return NextResponse.json({ message: 'Error processing report' }, { status: 400 })
  }
}

import type { MetadataRoute } from 'next'

import { routeData } from '@/lib/sitemap-data'

export default function sitemap(): MetadataRoute.Sitemap {
  const baseUrl = 'https://ponyclub.gr'

  const routes = routeData.map(({ route, fileMtime }) => ({
    url: `${baseUrl}${route}`,
    lastModified: new Date(fileMtime * 1000).toISOString(),
    changeFrequency: 'weekly' as const,
    priority: route === '' ? 1 : 0.8,
  }))

  // Generate localized routes for i18n
  const locales = ['en', 'el']
  const localizedRoutes: MetadataRoute.Sitemap = []

  routeData.forEach(({ route, fileMtime }) => {
    locales.forEach(locale => {
      // Ensure the path starts with a slash if it's not empty
      const pathSegment = route.startsWith('/') ? route : `/${route}`
      // Handle the root path correctly for locales
      const finalPath = route === '' ? `/${locale}` : `/${locale}${pathSegment}`

      localizedRoutes.push({
        url: `${baseUrl}${finalPath}`,
        lastModified: new Date(fileMtime * 1000).toISOString(),
        changeFrequency: 'weekly' as const,
        priority: route === '' ? 1 : 0.8,
      })
    })
  })

  return localizedRoutes
}

import { Roboto_Slab } from 'next/font/google'

// ResponsiveNavigation and Link/OptimizedImage for logo are no longer needed here directly.
// SiteHeader is now part of PageLayout.
// OptimizedImage might be used elsewhere if other image components are part of the content.
import { Container } from './ui/Container' // Import the Container component
import { OptimizedImage } from './ui/OptimizedImage'

// Define Roboto Slab font instance
const robotoSlab = Roboto_Slab({
  subsets: ['latin', 'greek'],
  variable: '--font-roboto-slab',
  weight: ['400', '700', '900'],
})

interface ActivityPageLayoutProps {
  title: string
  subtitle: string
  descriptionTitle: string
  descriptionContent: React.ReactNode
  detailsTitle: string
  detailsContent: React.ReactNode
  pricingTitle: string
  pricingContent: React.ReactNode
  showBookingButton?: boolean
  useSingleColumn?: boolean
  fullWidthContent?: boolean
}

export default function ActivityPageLayout({
  title,
  subtitle,
  descriptionTitle,
  descriptionContent,
  detailsTitle,
  detailsContent,
  pricingTitle,
  pricingContent,
  showBookingButton = true,
  useSingleColumn = false,
  fullWidthContent = false,
}: ActivityPageLayoutProps) {
  // Determine content max-width class based on column preference
  const contentMaxWidthClass = useSingleColumn ? 'max-w-none' : 'max-w-none'

  return (
    <>
      {/*
        SiteHeader is now rendered by PageLayout.
        The main PageLayout's <main> tag has pt-20 and the background color.
        This component's content will be rendered inside that <main> tag.
        The containerClasses will define the layout for the content of this specific activity page.
        Adjusting pt-24 to pt-4 in containerClasses to provide a small top padding for the content block itself,
        relative to the PageLayout's main content area.
      */}
      <Container className='py-6'>
        <div className='flex flex-col gap-8'>
          {/* Description Section */}
          {descriptionContent && (
            <div className='relative bg-white/80 backdrop-blur-sm p-6 rounded-lg shadow-lg border border-amber-100/70 hover:shadow-xl transition-shadow duration-300'>
              <div className={`prose ${contentMaxWidthClass} text-gray-700`}>{descriptionContent}</div>
              <div className='absolute -inset-[1px] -z-10 rounded-lg bg-gradient-to-tr from-amber-200/20 via-white/50 to-[#6b8362]/20 blur-sm'></div>
            </div>
          )}

          {/* Details Section - Always inside container */}
          {detailsTitle && detailsContent && (
            <div className='relative bg-white/80 backdrop-blur-sm p-6 rounded-lg shadow-lg border border-amber-100/70 hover:shadow-xl transition-shadow duration-300'>
              <h2
                className={`${robotoSlab.variable} font-roboto-slab text-2xl font-bold text-amber-800 mb-4 relative inline-block`}
              >
                {detailsTitle}
                <div className='absolute -bottom-1 left-0 w-full h-[2px] bg-gradient-to-r from-transparent via-amber-500/50 to-transparent'></div>
              </h2>
              <div className={`prose ${contentMaxWidthClass} text-gray-700`}>{detailsContent}</div>
              <div className='absolute -inset-[1px] -z-10 rounded-lg bg-gradient-to-tr from-amber-200/20 via-white/50 to-[#6b8362]/20 blur-sm'></div>
            </div>
          )}

          {/* Pricing Section */}
          {pricingTitle && pricingContent && (
            <div className='relative bg-white/80 backdrop-blur-sm p-6 rounded-lg shadow-lg border border-amber-100/70 hover:shadow-xl transition-shadow duration-300'>
              <h2
                className={`${robotoSlab.variable} font-roboto-slab text-2xl font-bold text-amber-800 mb-4 relative inline-block`}
              >
                {pricingTitle}
                <div className='absolute -bottom-1 left-0 w-full h-[2px] bg-gradient-to-r from-transparent via-amber-500/50 to-transparent'></div>
              </h2>
              <div className={`prose ${contentMaxWidthClass} text-gray-700`}>{pricingContent}</div>
              <div className='absolute -inset-[1px] -z-10 rounded-lg bg-gradient-to-tr from-amber-200/20 via-white/50 to-[#6b8362]/20 blur-sm'></div>
            </div>
          )}

          {/* If pricingTitle is not provided but pricingContent is, show just the content */}
          {!pricingTitle && pricingContent && (
            <div className='relative bg-white/80 backdrop-blur-sm p-6 rounded-lg shadow-lg border border-amber-100/70 hover:shadow-xl transition-shadow duration-300'>
              <div className={`prose ${contentMaxWidthClass} text-gray-700`}>{pricingContent}</div>
              <div className='absolute -inset-[1px] -z-10 rounded-lg bg-gradient-to-tr from-amber-200/20 via-white/50 to-[#6b8362]/20 blur-sm'></div>
            </div>
          )}
        </div>
      </Container>
    </>
  )
}

'use client'

import type { IFrameOptions } from 'iframe-resizer'
import { iframeResizer, IFramePage } from 'iframe-resizer' // Types for v3/v4
import React, { useEffect, useRef } from 'react'

import { useLanguage } from '@/contexts/language-context'
import { bokunLangMap } from '@/lib/bokun-lang'

type BokunWidgetProps = {
  experienceId: string
  partialView?: number
}

export default function BokunWidget({ experienceId, partialView = 1 }: BokunWidgetProps) {
  const { language } = useLanguage()
  const bokunLang = bokunLangMap[language] || 'en'
  const widgetContainerRef = useRef<HTMLDivElement>(null)

  const baseUrl = 'https://widgets.bokun.io/online-sales/c078b762-6f7f-474f-8edb-bdd1bdb7d12a/experience'
  const bokunWidgetSrcUrl = `${baseUrl}/${experienceId}?partialView=${partialView}&lang=${bokunLang}`

  useEffect(() => {
    const containerElement = widgetContainerRef.current
    if (!containerElement) {
      console.log('[Bokun Widget Parent] Container ref not found.')
      return
    }

    console.log('[Bokun Widget Parent] useEffect triggered. Container element:', containerElement)

    const options: IFrameOptions & {
      onMessage?: (messageData: { iframe: HTMLIFrameElement; message: any }) => void
      onResized?: (sizeData: { iframe: HTMLIFrameElement; height: number; width: number; type: string }) => void
      onInit?: (iFrameEl: HTMLIFrameElement) => void
    } = {
      log: process.env.NODE_ENV === 'development', // Enable logs only in dev
      checkOrigin: false, // Be cautious in production
      // Updated to use current callback names
      onMessage: (messageData: { iframe: HTMLIFrameElement; message: any }) => {
        console.log('[Bokun Widget Parent] Received message from iframe (onMessage):', messageData.message)
        // Potentially handle specific messages if needed in the future
      },
      onResized: (sizeData: { iframe: HTMLIFrameElement; height: number; width: number; type: string }) => {
        console.log('[Bokun Widget Parent] iframeResizer onResized:', sizeData)
      },
      onInit: (iFrameEl: HTMLIFrameElement) => {
        console.log('[Bokun Widget Parent] iframeResizer onInit: iframe is ready.', iFrameEl)
      },
    }

    // Ensure window.iFrameResizer exists and has onMessage, onResized, onInit
    // This is a workaround for the "onMessage function not defined" warning
    // if the Bokun widget's internal iframe-resizer script is looking for a global function.
    if (!(window as any).iFrameResizer) {
      ;(window as any).iFrameResizer = {}
    }
    ;(window as any).iFrameResizer.onMessage = (messageData: { iframe: HTMLIFrameElement; message: any }) => {
      console.log(
        '[Bokun Widget Parent] window.iFrameResizer.onMessage received:',
        messageData.message,
        'from iframe:',
        messageData.iframe.id
      )
      // Call the options.onMessage handler as well
      if (options.onMessage) {
        options.onMessage(messageData)
      }
    }
    ;(window as any).iFrameResizer.onResized = options.onResized
    ;(window as any).iFrameResizer.onInit = options.onInit

    // 1. Initialize iframeResizer for the iframe embedded within this component (catalogue view)
    const catalogueObserver = new MutationObserver((mutationsList, observerInstance) => {
      const catalogueIframe = containerElement.querySelector('iframe')
      if (catalogueIframe && !catalogueIframe.dataset.resizerAttached) {
        console.log('[Bokun Widget Parent] Found catalogue iframe, initializing iframeResizer:', catalogueIframe)
        try {
          iframeResizer(options, catalogueIframe)
          catalogueIframe.dataset.resizerAttached = 'true' // Mark as initialized
          console.log('[Bokun Widget Parent] iframeResizer initialized for catalogue iframe.')
        } catch (error) {
          console.error('[Bokun Widget Parent] Error initializing iframeResizer for catalogue iframe:', error)
        }
        observerInstance.disconnect() // Stop observing once the catalogue iframe is found and initialized
      } else if (catalogueIframe?.dataset.resizerAttached) {
        console.log('[Bokun Widget Parent] Catalogue iframe already has resizer attached.')
        observerInstance.disconnect()
      } else {
        console.log('[Bokun Widget Parent] Catalogue iframe not found yet in MutationObserver callback.')
      }
    })

    console.log('[Bokun Widget Parent] Starting MutationObserver on container for catalogue iframe.')
    catalogueObserver.observe(containerElement, { childList: true, subtree: true })

    // 2. Initialize iframeResizer for the cart iframe injected into the body by Bokun
    const bodyObserver = new MutationObserver((mutationsList, observerInstance) => {
      const cartIframe = document.getElementById('bokun-widgets-cart') as HTMLIFrameElement | null
      // Check if the cart iframe exists and hasn't been initialized yet
      if (cartIframe && !cartIframe.dataset.resizerAttached) {
        console.log('[Bokun Widget Parent] Found cart iframe in body, initializing iframeResizer:', cartIframe)
        try {
          iframeResizer(options, cartIframe)
          cartIframe.dataset.resizerAttached = 'true' // Mark as initialized
          console.log('[Bokun Widget Parent] iframeResizer initialized for cart iframe.')
          // Optional: Disconnect if you only expect one cart iframe instance
          // observerInstance.disconnect();
        } catch (error) {
          console.error('[Bokun Widget Parent] Error initializing iframeResizer for cart iframe:', error)
        }
      } else if (cartIframe?.dataset.resizerAttached) {
        // console.log("[Bokun Widget Parent] Cart iframe already has resizer attached.");
      }
    })

    console.log('[Bokun Widget Parent] Starting MutationObserver on document body for cart iframe.')
    // Observe the body for additions/removals, including the cart iframe
    bodyObserver.observe(document.body, { childList: true, subtree: false }) // Observe direct children of body

    // Cleanup function
    return () => {
      console.log('[Bokun Widget Parent] useEffect cleanup: Disconnecting observers.')
      catalogueObserver.disconnect()
      bodyObserver.disconnect()
      // Note: iframe-resizer v4 might need manual cleanup if iframes are removed dynamically.
      // e.g., iframeResizer.iframeResizer.close(iframeElement)
      // For now, relying on component unmount and observer disconnect.
    }
  }, [bokunWidgetSrcUrl]) // Re-run if the URL changes

  return (
    <>
      <div
        ref={widgetContainerRef}
        className='bokunWidget'
        data-src={bokunWidgetSrcUrl}
        data-lang={bokunLang}
        style={{ width: '100%', minHeight: '500px' }} // Ensure div has dimensions
      >
        {/* Bokun's script will inject the iframe here */}
      </div>
      <noscript>Please enable javascript in your browser to book</noscript>
    </>
  )
}

'use client'

import dynamic from 'next/dynamic'
import React, { useState, useEffect, useRef } from 'react'

// Dynamically import BokunWidget with ssr: false and a loading placeholder
const BokunWidget = dynamic(() => import('@/components/BokunWidget'), {
  ssr: false,
  loading: () => <div className='h-96 w-full bg-gray-200 animate-pulse rounded-lg' />,
})

type DynamicBokunWidgetProps = {
  experienceId: string
  partialView?: number
}

export default function DynamicBokunWidget({ experienceId, partialView }: DynamicBokunWidgetProps) {
  const [shouldLoad, setShouldLoad] = useState(false)
  const ref = useRef<HTMLDivElement>(null)

  useEffect(() => {
    if (!ref.current) return

    const observer = new IntersectionObserver(
      ([entry]) => {
        // Load when the placeholder is intersecting or nearly intersecting
        if (entry.isIntersecting) {
          setShouldLoad(true)
          observer.disconnect() // Stop observing once loaded
        }
      },
      {
        rootMargin: '200px 0px', // Load when 200px away from viewport
        threshold: 0.01, // Trigger even if only 1% is visible
      }
    )

    observer.observe(ref.current)

    // Cleanup observer on component unmount
    return () => {
      if (observer && ref.current) {
        observer.unobserve(ref.current)
      }
      observer.disconnect()
    }
  }, []) // Empty dependency array ensures this runs only once on mount

  return (
    <div ref={ref} style={{ minHeight: '384px' }}>
      {' '}
      {/* Wrapper div for observer, with min-height matching placeholder */}
      {shouldLoad ? (
        <BokunWidget experienceId={experienceId} partialView={partialView} />
      ) : (
        // Render the loading placeholder defined in dynamic import
        <div className='h-96 w-full bg-gray-200 animate-pulse rounded-lg' />
      )}
    </div>
  )
}

'use client'

import dynamic from 'next/dynamic'
import React, { useState, useEffect, useRef } from 'react'

// Dynamically import GoogleMap with ssr: false
const GoogleMapComponent = dynamic(() => import('@/components/google-map'), {
  ssr: false,
  loading: () => (
    <div className='h-[400px] w-full bg-gray-200 animate-pulse rounded-lg shadow-xl border border-amber-100/70' />
  ),
})

export default function DynamicGoogleMap() {
  const [isIntersecting, setIsIntersecting] = useState(false)
  const mapRef = useRef<HTMLDivElement | null>(null)

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsIntersecting(true)
          observer.unobserve(entry.target) // Stop observing once visible
        }
      },
      {
        rootMargin: '100px', // Load when the map is 100px away from viewport
      }
    )

    if (mapRef.current) {
      observer.observe(mapRef.current)
    }

    return () => {
      if (mapRef.current) {
        observer.unobserve(mapRef.current)
      }
    }
  }, [])

  return (
    <div ref={mapRef} className='h-[400px] w-full'>
      {' '}
      {/* Container for observer and to maintain space */}
      {isIntersecting ? (
        <GoogleMapComponent />
      ) : (
        <div className='h-[400px] w-full bg-gray-200 animate-pulse rounded-lg shadow-xl border border-amber-100/70' />
      )}
    </div>
  )
}

'use client'

import { Waves, MountainSnow, Sailboat, User } from 'lucide-react'
import { useState, useEffect } from 'react'

import BookingButton from '@/components/client/BookingButton'
import { OptimizedImage } from '@/components/ui/OptimizedImage'
import { useLanguage } from '@/contexts/language-context'

interface PackageCardProps {
  title: string
  badge: string
  activities: {
    primary: string
    riding: string
    hiking: string
  }
  pricing: {
    adults?: string
    children?: string
    perPerson?: string
  }
  images: {
    main: string
    top: string
    bottom: string
  }
  bookingId: string
  dataSrc: string
  bookNowText: string
  packageName: string
  packagePrice: string
  trackingLabel: string
  variant: 'green' | 'amber'
}

export default function EnhancedPackageCard({
  title,
  badge,
  activities,
  pricing,
  images,
  bookingId,
  dataSrc,
  bookNowText,
  packageName,
  packagePrice,
  trackingLabel,
  variant,
}: PackageCardProps) {
  const [nonce, setNonce] = useState('')

  useEffect(() => {
    // Get nonce from meta tag
    const metaNonce = document.querySelector('meta[name="csp-nonce"]')?.getAttribute('content') || ''
    setNonce(metaNonce)
  }, [])
  const colorTheme = {
    green: {
      gradient: 'from-emerald-400 via-teal-500 to-cyan-600',
      badge: 'bg-yellow-400 text-yellow-900',
      glass: 'bg-white/10',
      border: 'border-white/15',
      icon: 'text-emerald-300',
      priceBox: 'bg-white/20',
      button: 'from-emerald-500 to-green-500 hover:from-emerald-600 hover:to-green-600',
      text: 'text-emerald-50',
    },
    amber: {
      gradient: 'from-amber-400 via-orange-500 to-red-500',
      badge: 'bg-blue-400 text-blue-900',
      glass: 'bg-white/10',
      border: 'border-white/15',
      icon: 'text-amber-300',
      priceBox: 'bg-white/20',
      button: 'from-amber-500 to-orange-500 hover:from-amber-600 hover:to-orange-600',
      text: 'text-amber-50',
    },
  }

  const theme = colorTheme[variant]

  const renderActivityIcon = (index: number) => {
    if (index === 0) {
      return variant === 'green' ? (
        <Waves className='w-3 h-3 sm:w-4 sm:h-4 md:w-5 md:h-5 text-white' />
      ) : (
        <Sailboat className='w-3 h-3 sm:w-4 sm:h-4 md:w-5 md:h-5 text-white' />
      )
    }
    return <MountainSnow className='w-3 h-3 sm:w-4 sm:h-4 md:w-5 md:h-5 text-white' />
  }

  return (
    <div className={`bg-gradient-to-tr ${theme.gradient} p-1 rounded-3xl shadow-2xl w-full max-w-md mx-auto`}>
      <div
        className={`${theme.glass} backdrop-blur-xl border ${theme.border} shadow-2xl rounded-3xl overflow-hidden w-full h-full flex flex-col`}
      >
        <div className='p-4 sm:p-6 md:p-8 flex flex-col h-full min-h-[580px] sm:min-h-[620px]'>
          {/* Header */}
          <div className='flex justify-between items-start mb-4 flex-shrink-0'>
            <h2 className='text-2xl sm:text-3xl font-bold text-white'>{title}</h2>
            <span
              className={`${theme.badge} text-xs sm:text-sm font-semibold px-3 py-1.5 rounded-full shadow-md whitespace-nowrap`}
            >
              {badge}
            </span>
          </div>

          {/* Image Mosaic */}
          <div className='mosaic-grid h-44 sm:h-52 mb-4 sm:mb-6 rounded-xl overflow-hidden border border-white/20 flex-shrink-0'>
            <div className='relative mosaic-main'>
              <OptimizedImage
                src={images.main}
                alt={`${title} main activity`}
                fill
                sizes='(max-width: 512px) 45vw, 240px'
                className='w-full h-full object-cover'
                imageType='default'
              />
            </div>
            <div className='relative mosaic-top'>
              <OptimizedImage
                src={images.top}
                alt={`${title} activity 2`}
                fill
