  const update = (props: ToasterToast) =>
    dispatch({
      type: 'UPDATE_TOAST',
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: 'DISMISS_TOAST', toastId: id })

  dispatch({
    type: 'ADD_TOAST',
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: open => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: 'DISMISS_TOAST', toastId }),
  }
}

export { useToast, toast }

import { useEffect } from 'react'

export function useBokunLanguage(lang: string) {
  useEffect(() => {
    if (typeof window === 'undefined') return
    const bokun = (window as any).BokunWidgets
    if (!bokun) return

    // Set global default for pop-up checkout (if supported)
    if (typeof bokun.setLanguage === 'function') {
      bokun.setLanguage(lang)
    }

    // Update all widgets and buttons with the new language
    document.querySelectorAll<HTMLElement>('.bokunWidget, .bokunButton').forEach(el => {
      // Update data-src URL with lang query param
      if (el.dataset.src) {
        const url = new URL(el.dataset.src, window.location.origin)
        url.searchParams.set('lang', lang)
        el.dataset.src = url.toString()
      }
      // Set data-lang attribute for newer widgets
      el.setAttribute('data-lang', lang)
    })

    // Reload widgets to apply language change
    if (typeof bokun.reload === 'function') {
      bokun.reload()
    }
  }, [lang])
}

#!/usr/bin/env node

const { execSync } = require('child_process')
const fs = require('fs')
const path = require('path')

/**
 * Get the last Git commit timestamp for a file
 * @param {string} filePath - The path to the file
 * @returns {number} - Unix timestamp of last commit affecting this file
 */
function getGitLastModified(filePath) {
  try {
    // Get the timestamp of the last commit that modified this file
    const gitCmd = `git log -1 --format="%ct" -- "${filePath}"`
    const result = execSync(gitCmd, { encoding: 'utf8' }).trim()
    return result ? parseInt(result) : Math.floor(Date.now() / 1000)
  } catch (error) {
    console.warn(`Could not get Git timestamp for ${filePath}, using current time`)
    return Math.floor(Date.now() / 1000)
  }
}

/**
 * Generate sitemap data for all routes
 */
function generateSitemapData() {
  // Define the routes and their corresponding file paths
  const routes = [
    { route: '', filePath: 'app/[locale]/page.tsx' },
    { route: '/kayaking', filePath: 'app/[locale]/kayaking/page.tsx' },
    { route: '/rafting', filePath: 'app/[locale]/rafting/page.tsx' },
    { route: '/riding', filePath: 'app/[locale]/riding/page.tsx' },
    { route: '/river-village', filePath: 'app/[locale]/river-village/page.tsx' },
    { route: '/trekking', filePath: 'app/[locale]/trekking/page.tsx' },

    { route: '/for-schools', filePath: 'app/[locale]/for-schools/page.tsx' },
    { route: '/kayak-rafting', filePath: 'app/[locale]/kayak-rafting/page.tsx' },
  ]

  const routeData = routes.map(({ route, filePath }) => {
    const lastModified = getGitLastModified(filePath)
    console.log(`${route || '/'}: ${new Date(lastModified * 1000).toISOString()}`)
    return {
      route,
      fileMtime: lastModified,
    }
  })

  // Generate the TypeScript content for sitemap data
  const tsContent = `// Auto-generated sitemap data - Do not edit manually
// Generated on: ${new Date().toISOString()}

export interface RouteData {
  route: string;
  fileMtime: number;
}

export const routeData: RouteData[] = ${JSON.stringify(routeData, null, 2)};
`

  // Write to a TypeScript file that can be imported by sitemap.ts
  const outputPath = path.join(__dirname, '../lib/sitemap-data.ts')
  fs.writeFileSync(outputPath, tsContent)

  console.log(`\n✅ Sitemap data generated successfully:`)
  console.log(`📁 Output: ${outputPath}`)
  console.log(`📊 Routes processed: ${routeData.length}`)
}

// Run if called directly
if (require.main === module) {
  generateSitemapData()
}

module.exports = { generateSitemapData }

