'use client'

import { MountainSnow, Sailboat, User, Waves } from 'lucide-react'
import { useEffect, useState } from 'react'

import BookingButton from '@/components/client/BookingButton'
import { OptimizedImage } from '@/components/ui/OptimizedImage'

interface PackageCardProps {
  title: string
  badge: string
  activities: {
    primary: string
    riding: string
    hiking: string
  }
  pricing: {
    adults?: string
    children?: string
    perPerson?: string
  }
  images: {
    main: string
    top: string
    bottom: string
  }
  bookingId: string
  dataSrc: string
  bookNowText: string
  packageName: string
  packagePrice: string
  trackingLabel: string
  variant: 'green' | 'amber'
}

export default function EnhancedPackageCard({
  title,
  badge,
  activities,
  pricing,
  images,
  bookingId,
  dataSrc,
  bookNowText,
  packageName,
  packagePrice,
  trackingLabel,
  variant,
}: PackageCardProps) {
  const [nonce, setNonce] = useState('')

  useEffect(() => {
    // Get nonce from meta tag
    const metaNonce = document.querySelector('meta[name="csp-nonce"]')?.getAttribute('content') || ''
    setNonce(metaNonce)
  }, [])
  const colorTheme = {
    green: {
      gradient: 'from-emerald-400 via-teal-500 to-cyan-600',
      badge: 'bg-yellow-400 text-yellow-900',
      glass: 'bg-white/10',
      border: 'border-white/15',
      icon: 'text-emerald-300',
      priceBox: 'bg-white/20',
      button: 'from-emerald-500 to-green-500 hover:from-emerald-600 hover:to-green-600',
      text: 'text-emerald-50',
    },
    amber: {
      gradient: 'from-amber-400 via-orange-500 to-red-500',
      badge: 'bg-blue-400 text-blue-900',
      glass: 'bg-white/10',
      border: 'border-white/15',
      icon: 'text-amber-300',
      priceBox: 'bg-white/20',
      button: 'from-amber-500 to-orange-500 hover:from-amber-600 hover:to-orange-600',
      text: 'text-amber-50',
    },
  }

  const theme = colorTheme[variant]

  const renderActivityIcon = (index: number) => {
    if (index === 0) {
      return variant === 'green' ? (
        <Waves
          className={`
            h-3 w-3 text-white
            sm:h-4 sm:w-4
            md:h-5 md:w-5
          `}
        />
      ) : (
        <Sailboat
          className={`
            h-3 w-3 text-white
            sm:h-4 sm:w-4
            md:h-5 md:w-5
          `}
        />
      )
    }
    return (
      <MountainSnow
        className={`
          h-3 w-3 text-white
          sm:h-4 sm:w-4
          md:h-5 md:w-5
        `}
      />
    )
  }

  return (
    <div
      className={`
        bg-gradient-to-tr
        ${theme.gradient}
        mx-auto w-full max-w-md rounded-3xl p-1 shadow-2xl
      `}
    >
      <div
        className={`
          ${theme.glass}
          border backdrop-blur-xl
          ${theme.border}
          flex h-full w-full flex-col overflow-hidden rounded-3xl shadow-2xl
        `}
      >
        <div
          className={`
            flex h-full min-h-[580px] flex-col p-4
            sm:min-h-[620px] sm:p-6
            md:p-8
          `}
        >
          {/* Header */}
          <div className='mb-4 flex flex-shrink-0 items-start justify-between'>
            <h2
              className={`
                text-2xl font-bold text-white
                sm:text-3xl
              `}
            >
              {title}
            </h2>
            <span
              className={`
                ${theme.badge}
                rounded-full px-3 py-1.5 text-xs font-semibold whitespace-nowrap
                shadow-md
                sm:text-sm
              `}
            >
              {badge}
            </span>
          </div>

          {/* Image Mosaic */}
          <div
            className={`
              mosaic-grid mb-4 h-44 flex-shrink-0 overflow-hidden rounded-xl
              border border-white/20
              sm:mb-6 sm:h-52
            `}
          >
            <div className='mosaic-main relative'>
              <OptimizedImage
                src={images.main}
                alt={`${title} main activity`}
                fill
                sizes='(max-width: 512px) 45vw, 240px'
                className='h-full w-full object-cover'
                imageType='default'
              />
            </div>
            <div className='mosaic-top relative'>
              <OptimizedImage
                src={images.top}
                alt={`${title} activity 2`}
                fill
                sizes='(max-width: 512px) 45vw, 240px'
                className='h-full w-full object-cover'
                imageType='default'
              />
            </div>
            <div className='mosaic-bottom relative'>
              <OptimizedImage
                src={images.bottom}
                alt={`${title} activity 3`}
                fill
                sizes='(max-width: 512px) 45vw, 240px'
                className='h-full w-full object-cover'
                imageType='default'
              />
            </div>
          </div>

          {/* Activities List */}
          <ul
            className={`
              space-y-2
              sm:space-y-3
              ${theme.text}
              mb-6 flex-grow
              sm:mb-8
            `}
          >
            {[activities.primary, activities.riding, activities.hiking].map((activity, index) => (
              <li
                key={index}
                className={`
                  flex items-center text-sm
                  sm:text-base
                `}
              >
                <span
                  className={`
                    inline-flex h-6 w-6 items-center justify-center
                    sm:h-8 sm:w-8
                    ${theme.icon}
                    mr-2 flex-shrink-0 rounded-full bg-white/20
                    sm:mr-3
                  `}
                >
                  {renderActivityIcon(index)}
                </span>
                <span className='flex-1'>{activity}</span>
              </li>
            ))}
          </ul>

          {/* Pricing */}
          <div
            className={`
              mb-6 flex-shrink-0 space-y-3
              sm:mb-8 sm:space-y-4
            `}
          >
            {pricing.adults && (
              <div
                className={`
                  flex items-center justify-between
                  ${theme.priceBox}
                  rounded-xl border border-white/10 p-3 shadow-inner
                  backdrop-blur-sm
                  sm:p-4
                `}
              >
                <p
                  className={`
                    text-base font-semibold text-white
                    sm:text-lg
                  `}
                >
                  Adults
                </p>
                <p
                  className={`
                    text-xl font-bold text-white
                    sm:text-2xl
                  `}
                >
                  {pricing.adults}
                </p>
              </div>
            )}

            {pricing.children && (
              <div
                className={`
                  flex items-center justify-between
                  ${theme.priceBox}
                  rounded-xl border border-white/10 p-3 shadow-inner
                  backdrop-blur-sm
                  sm:p-4
                `}
              >
                <div>
                  <p
                    className={`
                      text-base font-semibold text-white
                      sm:text-lg
                    `}
                  >
                    Children
                  </p>
                  <p
                    className={`
                      text-xs text-white/80
                      sm:text-sm
                    `}
                  >
                    under 12 years old
                  </p>
                </div>
                <p
                  className={`
                    text-xl font-bold text-white
                    sm:text-2xl
                  `}
                >
                  {pricing.children}
                </p>
              </div>
            )}

            {pricing.perPerson && (
              <div
                className={`
                  flex items-center justify-between
                  ${theme.priceBox}
                  rounded-xl border border-white/10 p-3 shadow-inner
                  backdrop-blur-sm
                  sm:p-4
                `}
              >
                <div className='flex items-center'>
                  <User
                    className={`
                      mr-2 h-4 w-4 text-white
                      sm:h-5 sm:w-5
                    `}
                  />
                  <p
                    className={`
                      text-base font-semibold text-white
                      sm:text-lg
                    `}
                  >
                    Per Person
                  </p>
                </div>
                <p
                  className={`
                    text-xl font-bold text-white
                    sm:text-2xl
                  `}
                >
                  {pricing.perPerson}
                </p>
              </div>
            )}

            {/* Add empty space for cards with less pricing info to maintain height */}
            {!pricing.children && !pricing.perPerson && (
              <div
                className={`
                  h-16
                  sm:h-20
                `}
              ></div>
            )}
            {!pricing.adults && !pricing.children && !pricing.perPerson && (
              <div
                className={`
                  h-32
                  sm:h-40
                `}
              ></div>
            )}
          </div>

          {/* Book Now Button */}
          <div className='mt-auto flex-shrink-0'>
            <BookingButton
              id={bookingId}
              dataSrc={dataSrc}
              className={`
                w-full bg-gradient-to-r
                ${theme.button}
                flex transform items-center justify-center rounded-xl px-4 py-3
                text-base font-semibold text-white shadow-lg transition
                duration-300 ease-in-out
                hover:scale-105 hover:shadow-xl
                sm:px-6 sm:py-3.5 sm:text-lg
              `}
              trackingLabel={trackingLabel}
              packageName={packageName}
              packagePrice={packagePrice}
            >
              {bookNowText}
              <svg
                className={`
                  ml-2 h-4 w-4
                  sm:h-5 sm:w-5
                `}
                fill='none'
                stroke='currentColor'
                viewBox='0 0 24 24'
              >
                <path strokeLinecap='round' strokeLinejoin='round' strokeWidth={2} d='M13 7l5 5m0 0l-5 5m5-5H6' />
              </svg>
            </BookingButton>
          </div>
        </div>
      </div>

      <style jsx nonce={nonce}>{`
        .mosaic-grid {
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          grid-template-rows: repeat(2, 1fr);
          gap: 6px;
        }
        .mosaic-main {
          grid-column: 1 / 2;
          grid-row: 1 / 3;
        }
        .mosaic-top {
          grid-column: 2 / 3;
          grid-row: 1 / 2;
        }
        .mosaic-bottom {
          grid-column: 2 / 3;
          grid-row: 2 / 3;
        }
      `}</style>
    </div>
  )
}

'use client'

import { useCallback, useRef } from 'react'

import { useGDPR } from '@/contexts/gdpr-context'

interface BookingButtonProps {
  id: string
  dataSrc: string
  className?: string
  children: React.ReactNode
  trackingLabel?: string // For identifying which button was clicked
  packageName?: string // For enhanced ecommerce tracking
  packagePrice?: string // For conversion value tracking
}

export default function BookingButton({
  id,
  dataSrc,
  className,
  children,
  trackingLabel = 'Unknown',
  packageName = 'Unknown Package',
  packagePrice = '0',
}: BookingButtonProps) {
  const clickedButtonRef = useRef<HTMLButtonElement | null>(null)
  const bokunReadyAttempts = useRef(0)
  const { consent } = useGDPR()

  const ensureBokunIsReadyAndOpen = useCallback(() => {
    if (
      window.BokunWidgets &&
      (typeof window.BokunWidgets.init === 'function' || typeof window.BokunWidgets.reinit === 'function')
    ) {
      // Check if a modal is already open (very basic check, might need refinement)
      if (document.querySelector('.bokunModalContainer') || document.querySelector('.bokun-modal-open')) {
        return
      }

      if (clickedButtonRef.current) {
        // It's possible Bokun's scripts have now attached proper listeners.
        // A direct click might be better than trying to call their internal modal functions.
        clickedButtonRef.current.click()
        clickedButtonRef.current = null // Clear after attempting
      }
      bokunReadyAttempts.current = 0 // Reset attempts
    } else if (bokunReadyAttempts.current < 30) {
      // Try for ~3 seconds
      bokunReadyAttempts.current++
      setTimeout(ensureBokunIsReadyAndOpen, 100)
    } else {
      bokunReadyAttempts.current = 0 // Reset attempts
    }
  }, [])

  // Comprehensive tracking function for GDPR-compliant analytics
  const trackBookingClick = useCallback(() => {
    if (typeof window === 'undefined' || !consent) return

    // Extract numeric price for conversion tracking
    const numericPrice = parseFloat(packagePrice.replace(/[^\d.]/g, '')) || 0

    // Google Analytics 4 Event Tracking (only if analytics consent given)
    if (window.gtag && consent.analytics) {
      // Standard GA4 event
      window.gtag('event', 'book_now_click', {
        event_category: 'Booking',
        event_label: trackingLabel,
        package_name: packageName,
        package_price: numericPrice,
        currency: 'EUR',
        button_id: id,
        page_location: window.location.href,
        page_title: document.title,
      })

      // Enhanced Ecommerce - Begin Checkout Event
      window.gtag('event', 'begin_checkout', {
        currency: 'EUR',
        value: numericPrice,
        items: [
          {
            item_id: id,
            item_name: packageName,
            item_category: 'Adventure Package',
            price: numericPrice,
            quantity: 1,
          },
        ],
      })

      // Google Ads Conversion Tracking
      const googleAdsConversionId = process.env.NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_ID
      const googleAdsConversionLabel = process.env.NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_LABEL

      if (googleAdsConversionId && googleAdsConversionLabel) {
        window.gtag('event', 'conversion', {
          send_to: `${googleAdsConversionId}/${googleAdsConversionLabel}`,
          value: numericPrice,
          currency: 'EUR',
          transaction_id: `booking_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        })
      } else if (process.env.NODE_ENV === 'development') {
        console.warn(
          '[Booking Tracking] Google Ads conversion tracking not configured. Please set NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_ID and NEXT_PUBLIC_GOOGLE_ADS_CONVERSION_LABEL environment variables.'
        )
      }
    }

    // Vercel Analytics (if available and analytics consent given)
    if (window.va && consent.analytics) {
      interface VercelAnalytics {
        (event: string, data: { name: string; data: Record<string, unknown> }): void
      }
      ;(window.va as VercelAnalytics)('event', {
        name: 'Book Now Click',
        data: {
          package: packageName,
          price: numericPrice,
          button_id: id,
          label: trackingLabel,
        },
      })
    }

    // Facebook Pixel (if available and marketing consent given)
    if (window.fbq && consent.marketing) {
      window.fbq('track', 'InitiateCheckout', {
        content_name: packageName,
        content_category: 'Adventure Package',
        value: numericPrice,
        currency: 'EUR',
      })
    }

    if (process.env.NODE_ENV === 'development') {
      console.log(
        `[Booking Tracking] ${trackingLabel} clicked - Package: ${packageName}, Price: â‚¬${numericPrice}`,
        'Consent:',
        consent
      )
    }
  }, [trackingLabel, packageName, packagePrice, id, consent])

  const handleBookNowClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    // Track the click immediately
    trackBookingClick()

    clickedButtonRef.current = event.currentTarget
    ensureBokunIsReadyAndOpen() // Direct call as loader is global
  }

  return (
    <button
      className={`
        bokunButton
        ${className}
      `}
      id={id}
      data-src={dataSrc}
      data-testid='widget-book-button'
      onClick={handleBookNowClick}
    >
      {children}
    </button>
  )
}

'use client'

import Script from 'next/script'
import { useEffect } from 'react'

import { useGDPR, CookieConsent } from '@/contexts/gdpr-context' // Import CookieConsent

interface GDPRGoogleAnalyticsProps {
  gaId: string
}

export default function GDPRGoogleAnalytics({ gaId }: GDPRGoogleAnalyticsProps) {
  const { consent } = useGDPR() // Get the consent object

  useEffect(() => {
    // This effect handles applying consent changes to gtag
    if (typeof window !== 'undefined' && window.gtag && consent) {
      window.gtag('consent', 'update', {
        analytics_storage: consent.analytics ? 'granted' : 'denied',
        ad_storage: consent.marketing ? 'granted' : 'denied', // Assuming marketing consent maps to ad_storage
        ad_user_data: consent.marketing ? 'granted' : 'denied',
        ad_personalization: consent.marketing ? 'granted' : 'denied',
      })
      console.log('[GDPR GA] Consent updated:', consent)
    }
  }, [consent]) // Re-run when consent object changes

  // Initialize gtag in a CSP-compliant way
  useEffect(() => {
    if (typeof window !== 'undefined') {
      // Initialize dataLayer and gtag function
      window.dataLayer = window.dataLayer || []
      function gtag(...args: unknown[]) {
        window.dataLayer?.push(args)
      }
      window.gtag = gtag

      // Set default consent to denied
      gtag('consent', 'default', {
        analytics_storage: 'denied',
        ad_storage: 'denied',
        ad_user_data: 'denied',
        ad_personalization: 'denied',
        wait_for_update: 500,
      })

      gtag('js', new Date())
      gtag('config', gaId, {
        page_title: document.title,
        page_location: window.location.href,
      })

      console.log('[GDPR GA] gtag initialized with default denied consent')
    }
  }, [gaId])

  return (
    <>
      {/* External gtag script - CSP compliant */}
      <Script
        id='ga-gtag-script'
        src={`https://www.googletagmanager.com/gtag/js?id=${gaId}`}
        strategy='afterInteractive'
        onLoad={() => {
          console.log('[GDPR GA] gtag.js loaded.')
        }}
        onError={() => {
          console.error('[GDPR GA] Failed to load gtag.js.')
        }}
      />
    </>
  )
}

