// Inspired by react-hot-toast library
import * as React from 'react'

import type { ToastActionElement, ToastProps } from '@/components/ui/toast'

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: 'ADD_TOAST',
  UPDATE_TOAST: 'UPDATE_TOAST',
  DISMISS_TOAST: 'DISMISS_TOAST',
  REMOVE_TOAST: 'REMOVE_TOAST',
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType['ADD_TOAST']
      toast: ToasterToast
    }
  | {
      type: ActionType['UPDATE_TOAST']
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType['DISMISS_TOAST']
      toastId?: ToasterToast['id']
    }
  | {
      type: ActionType['REMOVE_TOAST']
      toastId?: ToasterToast['id']
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: 'REMOVE_TOAST',
      toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case 'ADD_TOAST':
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case 'UPDATE_TOAST':
      return {
        ...state,
        toasts: state.toasts.map(t => (t.id === action.toast.id ? { ...t, ...action.toast } : t)),
      }

    case 'DISMISS_TOAST': {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach(toast => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map(t =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case 'REMOVE_TOAST':
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter(t => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach(listener => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, 'id'>

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: 'UPDATE_TOAST',
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: 'DISMISS_TOAST', toastId: id })

  dispatch({
    type: 'ADD_TOAST',
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: open => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: 'DISMISS_TOAST', toastId }),
  }
}

export { useToast, toast }

'use client'

import React, { createContext, useContext, useEffect, useState } from 'react'

export interface CookieConsent {
  necessary: boolean
  analytics: boolean
  marketing: boolean
}

interface GDPRContextType {
  consent: CookieConsent | null
  showBanner: boolean
  showCustomize: boolean
  acceptAll: () => void
  rejectAll: () => void
  saveCustom: (consent: CookieConsent) => void
  openCustomize: () => void
  closeBanner: () => void
}

const GDPRContext = createContext<GDPRContextType | undefined>(undefined)

const CONSENT_COOKIE_NAME = 'ponyclub-cookie-consent'
const CONSENT_VERSION = '1.0'

export function GDPRProvider({ children }: { children: React.ReactNode }) {
  const [consent, setConsent] = useState<CookieConsent | null>(null)
  const [showBanner, setShowBanner] = useState(false)
  const [showCustomize, setShowCustomize] = useState(false)

  // Load consent from localStorage on mount
  useEffect(() => {
    if (typeof window === 'undefined') return

    const savedConsent = localStorage.getItem(CONSENT_COOKIE_NAME)
    if (savedConsent) {
      try {
        const parsed = JSON.parse(savedConsent)
        if (parsed.version === CONSENT_VERSION) {
          setConsent(parsed.consent)
          // Apply consent to tracking scripts
          applyConsent(parsed.consent)
        } else {
          // Version mismatch, show banner again
          setShowBanner(true)
        }
      } catch (error) {
        console.error('Error parsing consent cookie:', error)
        setShowBanner(true)
      }
    } else {
      // No consent found, show banner
      setShowBanner(true)
    }
  }, [])

  const saveConsent = (newConsent: CookieConsent) => {
    const consentData = {
      consent: newConsent,
      version: CONSENT_VERSION,
      timestamp: new Date().toISOString(),
    }

    localStorage.setItem(CONSENT_COOKIE_NAME, JSON.stringify(consentData))
    setConsent(newConsent)
    setShowBanner(false)
    setShowCustomize(false)

    // Apply consent to tracking scripts
    applyConsent(newConsent)

    // Track consent choice
    trackConsentChoice(newConsent)
  }

  const acceptAll = () => {
    saveConsent({
      necessary: true,
      analytics: true,
      marketing: true,
    })
  }

  const rejectAll = () => {
    saveConsent({
      necessary: true, // Always true - required for site functionality
      analytics: false,
      marketing: false,
    })
  }

  const saveCustom = (customConsent: CookieConsent) => {
    saveConsent({
      ...customConsent,
      necessary: true, // Always true - required for site functionality
    })
  }

  const openCustomize = () => {
    setShowCustomize(true)
  }

  const closeBanner = () => {
    setShowBanner(false)
    setShowCustomize(false)
  }

  return (
    <GDPRContext.Provider
      value={{
        consent,
        showBanner,
        showCustomize,
        acceptAll,
        rejectAll,
        saveCustom,
        openCustomize,
        closeBanner,
      }}
    >
      {children}
    </GDPRContext.Provider>
  )
}

export function useGDPR() {
  const context = useContext(GDPRContext)
  if (context === undefined) {
    throw new Error('useGDPR must be used within a GDPRProvider')
  }
  return context
}

// Apply consent to tracking scripts
function applyConsent(consent: CookieConsent) {
  if (typeof window === 'undefined') return

  // Google Analytics
  if (window.gtag) {
    window.gtag('consent', 'update', {
      analytics_storage: consent.analytics ? 'granted' : 'denied',
      ad_storage: consent.marketing ? 'granted' : 'denied',
      ad_user_data: consent.marketing ? 'granted' : 'denied',
      ad_personalization: consent.marketing ? 'granted' : 'denied',
    })
  }

  // Facebook Pixel
  if (window.fbq && !consent.marketing) {
    // Disable Facebook Pixel if marketing consent is denied
    window.fbq('consent', 'revoke')
  } else if (window.fbq && consent.marketing) {
    window.fbq('consent', 'grant')
  }

  console.log('[GDPR] Consent applied:', consent)
}

// Track consent choice for analytics
function trackConsentChoice(consent: CookieConsent) {
  if (typeof window === 'undefined') return

  // Only track if analytics consent is given
  if (consent.analytics && window.gtag) {
    window.gtag('event', 'consent_update', {
      event_category: 'GDPR',
      analytics_consent: consent.analytics,
      marketing_consent: consent.marketing,
      consent_method: 'banner',
    })
  }
}

